/* Customized Javascript 
-=-=-=-=-=-=-=-=-=-=-=-=*/
$(document).ready(function(){
    
    // Custom Functions initialization which are coded at bottom of this script.
    wrapperPadding();
    
    // Global Background SetUp
    $('.set-bg').each(function(){
        if($(this).find('.get-bg').length > 0){
            $(this).css('background-image','url('+$(this).find('.get-bg img').attr('src')+')');
        }else{
            $(this).css('background-image','url('+$(this).find('img').attr('src')+')');
        }
    });
   
    
    $('.teamPopUpContent .socialIcons > a').each(function(){
       
       var txt7 = $(this).attr('href');
       if(txt7 == ''){ 
            $(this).hide();
       }
    });
    
    
    // Responsive Mobile Menu 
    $('.custom-menu-primary').addClass('js-enabled');
    $('.custom-menu-primary .hs-menu-wrapper').before('<div class="mobile-trigger"><i></i></div>');
    $('.custom-menu-primary .flyouts .hs-item-has-children > a').after(' <div class="child-trigger"><i></i></div>');
    $('.mobile-trigger').click(function() {
        $(this).next('.custom-menu-primary .hs-menu-wrapper').slideToggle(250);
        $('body').toggleClass('mobile-open');
        $('.child-trigger').removeClass('child-open');
        $('.hs-menu-children-wrapper').slideUp(250);
        return false;
    });
    $('.child-trigger').click(function() {
        $(this).parent().siblings('.hs-item-has-children').find('.child-trigger').removeClass('child-open');
        $(this).parent().siblings('.hs-item-has-children').find('.hs-menu-children-wrapper').slideUp(250);
        $(this).next('.hs-menu-children-wrapper').slideToggle(250);
        $(this).next('.hs-menu-children-wrapper').children('.hs-item-has-children').find('.hs-menu-children-wrapper').slideUp(250);
        $(this).next('.hs-menu-children-wrapper').children('.hs-item-has-children').find('.child-trigger').removeClass('child-open');
        $(this).toggleClass('child-open');
        return false;
    });
    
    
    // to give unique dynamic id to each member and than show description on popUp in About Page
    if($('.aboutTeamBlock').length > 0){
        var blockCount = 0;
        $('.aboutTeamBlock').each(function(){
            blockCount = blockCount + 1;
            $(this).find('.popUpLink').attr('href','#PopUpBlock'+blockCount);
            $(this).find('.teamPopUpContent').attr('id','PopUpBlock'+blockCount);
        });
        $('.popUpLink').fancybox();
    }
    
    // to insert featured image before title in blog sidebar post listing
   $('.sidebarRss .hs-with-featured-image').each(function(){
      $(this).find('.hs-rss-item-image-wrapper').insertBefore($(this).find('.hs-rss-item-text')); 
   });
    
    // give placeholder to search field
    $('div[role="search"] input[type="text"]').attr('placeholder','Search Here');
    
    // to remove Featured image from Blog Post as its already at top of post 
    if($('.blog-post-wrapper .ListingfeaturedImage').length > 0){
            var featuredImg = $('.blog-post-wrapper .ListingfeaturedImage img').attr('src');
            featuredImg =  featuredImg.split('?')[0];
            featuredImg =  featuredImg.split('/');
            featuredImg = featuredImg[featuredImg.length-1];
        $('.blog-post-wrapper .post-body img').each(function(){
            var postImg = $(this).attr('src');
            postImg =  postImg.split('?')[0];
            postImg =  postImg.split('/');
            postImg = postImg[postImg.length-1];
            if( featuredImg == postImg ){
                $(this).hide();
                $(this).parent('p').remove();
            }
        });
    }
    
    // Filter All Link in Blog Option 2 and adding active class to the topic
    if($('body').hasClass('blogv2')){
        $('.postFilter ul').prepend($('.filterAllLink').html());
        $('.postFilter a').each(function(){
          if($(this).attr('href') == (window.location) ){
              $(this).addClass('active');
          } 
        });
    }
    
    // Home page 2 Testimonials slider
    if($('.testimonialBlock').length > 0){
        $('.testimonialBlock').parent().parent().addClass('testimonialSlider').slick({
            autoplay: true,
            arrows:false,
            dots:true
        });
    }
    
    
}); // <<<<<< Document Ready Ends Here

