{##########################
###      CSS Styles     ###
##########################}
<style>

    /* Show Web Font (does not work in all clients) */
    @import url('https://fonts.googleapis.com/css?family=Montserrat:400,700');
    [style*="sans-serif"] {
        font-family: 'Montserrat', Helvetica, Arial, Verdana, Trebuchet MS !important;
    }

    /* Constrain Images to the width of the email */
    img {
        max-width: 600px !important;
        height: auto !important;
    }

    .footer-text a, #footer a {
        color: #ffffff !important;
    }

    /* Forces no padding on email (does not work in all clients) */
    body #headerTable {
        padding-right: 0px !important;
    }
    body, body > table, #contentCell, .widget-type-email_view_as_web_page {
        background-color: #efefef !important;
    }
    body #contentTableOuter {
        box-shadow: none !important;
        border: none !important;
        border-bottom: none !important;
        padding: 0px !important;
    }

    /* Responsive Styles for mail clients that support them */
    @media only screen and (max-width: 650px) {
        /* Fix for Android Mobile Devices */
        .android-spacer {
            letter-spacing: initial !important;
        }

        /* Makes columns mobile responsive */
        .full {
            display: block;
            min-height: 230px;
            width: 100%;
        }

        .h-space {
            width: 10% !important;
        }
    }
</style>
<div style="letter-spacing: 596px; line-height: 0; mso-hide: all" class="android-spacer">&nbsp;</div>
